import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import { getDatabase, ref, onValue } from "firebase/database";

const Collection = () => {
  const [objects, setObjects] = useState([]);
  const [filters, setFilters] = useState({ keyword: "", category: "", material: "" });
  const location = useLocation();
  const query = new URLSearchParams(location.search).get("search");

  useEffect(() => {
    const db = getDatabase();
    const objectsRef = ref(db, "objects");

    onValue(objectsRef, (snapshot) => {
      const data = snapshot.val();

      // Ensure data is present and map it to a usable array format
      const filtered = Object.keys(data || {}).map((key) => data[key]).filter((obj) => {
        // Safely handle cases where title is null or undefined
        const title = obj.title || ""; // Default to an empty string if title is null
        return title.toLowerCase().includes(query?.toLowerCase() || "");
      });

      setObjects(filtered);
    });
  }, [query]);

  const handleFilterChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  return (
    <div className="collection-page grid grid-cols-4 gap-4">
      {/* Filters Sidebar */}
      <aside className="col-span-1 bg-gray-100 p-4">
        <h2 className="font-bold mb-4">Filters</h2>
        <input
          name="keyword"
          placeholder="Keyword"
          value={filters.keyword}
          onChange={handleFilterChange}
          className="border rounded p-2 mb-4 w-full"
        />
        <select
          name="category"
          onChange={handleFilterChange}
          className="border rounded p-2 mb-4 w-full"
        >
          <option value="">All Categories</option>
          <option value="Art">Art</option>
          <option value="Book">Book</option>
        </select>
        <select
          name="material"
          onChange={handleFilterChange}
          className="border rounded p-2 mb-4 w-full"
        >
          <option value="">All Materials</option>
          <option value="Paper">Paper</option>
          <option value="Gold">Gold</option>
        </select>
      </aside>

      {/* Objects Grid */}
      <main className="col-span-3 grid grid-cols-3 gap-4">
        {objects.map((obj, index) => (
          <Link
            key={index}
            to={`/object/${obj.object_id}`}
            className="block border p-4 bg-white"
          >
            <img
              src={obj.thumbnailUrl || "default-thumbnail.jpg"} // Use a fallback thumbnail if missing
              alt={obj.title || "Unknown Title"} // Use fallback title
              className="w-full h-40 object-cover mb-2"
            />
            <h3 className="text-lg font-bold">{obj.title || "Untitled"}</h3>
            <p>{obj.object_type || "Unknown Type"}</p>
          </Link>
        ))}
      </main>
    </div>
  );
};

export default Collection;


