import React, { useState, useEffect } from "react";
import { fetchData } from "../utils/fetchData";

const SearchPage = () => {
  const [query, setQuery] = useState("");
  const [allRecords, setAllRecords] = useState([]); // Store all records
  const [filteredResults, setFilteredResults] = useState([]); // Store filtered records

  // Fetch all records when the component mounts
  useEffect(() => {
    const loadRecords = async () => {
      try {
        const records = await fetchData();
        setAllRecords(records);
        setFilteredResults(records); // Show all records initially
      } catch (error) {
        console.error("Error fetching records:", error);
      }
    };

    loadRecords();
  }, []);

  // Perform filtering whenever the query changes
  useEffect(() => {
    if (query.trim() === "") {
      setFilteredResults(allRecords); // Show all records if the query is empty
    } else {
      const lowerCaseQuery = query.toLowerCase();
      const filtered = allRecords.filter((record) =>
        record.object_title?.toLowerCase().includes(lowerCaseQuery) ||
        record.object_type?.toLowerCase().includes(lowerCaseQuery) ||
        record.object_id?.toLowerCase().includes(lowerCaseQuery) ||
        record.title?.toLowerCase().includes(lowerCaseQuery)
      );
      setFilteredResults(filtered);
    }
  }, [query, allRecords]);

  return (
    <div className="search-page" style={{ display: "flex", flexDirection: "column", alignItems: "center", height: "80vh", paddingTop: "2rem" }}>
      <h1 style={{ fontSize: "3rem", fontWeight: "bold", marginBottom: "1rem" }}>Search the Collection</h1>
      <input
        type="text"
        placeholder="Search by title, object type, or ID..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        style={{
          fontSize: "1.5rem",
          padding: "0.75rem",
          width: "60%",
          marginBottom: "1rem",
          border: "1px solid #ccc",
          borderRadius: "8px",
        }}
      />
      <div style={{ marginTop: "2rem", width: "80%", maxHeight: "50vh", overflowY: "auto" }}>
        {filteredResults.length > 0 ? (
          <ul style={{ fontSize: "1.2rem", listStyle: "none", padding: 0 }}>
            {filteredResults.map((item, index) => (
              <li key={index} style={{ marginBottom: "1rem", borderBottom: "1px solid #ddd", paddingBottom: "1rem" }}>
                <strong>Title:</strong> {item.title || "N/A"} | <strong>Object Type:</strong> {item.object_type || "N/A"} | <strong>ID:</strong> {item.object_id || "N/A"}
              </li>
            ))}
          </ul>
        ) : (
          <p style={{ fontSize: "1.2rem", color: "#888" }}>No results found</p>
        )}
      </div>
    </div>
  );
};

